mapper.update:
UPDATE ask
SET ask_title = #{askTitle}, ask_content = #{askContent}
WHERE ask_no = #{askNo}

mapper.select:

SELECT a.*
FROM (
    SELECT a.*, rownum rn
    FROM (
        SELECT *
        FROM ask
        WHERE LOWER(ask_title) LIKE '%' || #{askTitle} || '%'
        ORDER BY ask_no DESC
    ) a
    WHERE rownum <= #{endNum}
) a
WHERE rn >= #{startNum}
