mapper.update:
update board set board_title =
#{boardTitle},board_content=#{boardContent},image_uuid=#{imageUuid}
where board_no = #{boardNo}

mapper.select:

SELECT a.*
FROM (
    SELECT a.*, rownum rn
    FROM (
        SELECT *
        FROM board
        WHERE LOWER(board_title) LIKE '%' || #{boardTitle} || '%'
        ORDER BY board_no DESC
    ) a
    WHERE rownum <= #{endNum}
) a
WHERE rn >= #{startNum}
